    private void kodeBarangActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_kodeBarangActionPerformed
     
     try {
            Connection connect = new Database().getKoneksi();
            String sql = "select * from barang where kode_barang='"+kodeBarang.getText()+"'";
            Statement statement = connect.createStatement();
            ResultSet result = statement.executeQuery(sql);
            
            while(result.next()){
                
                kodeBarang1.setText(result.getString("jml_beli"));
                kodeBarang2.setText(result.getString("harga_jual"));    
                }
            } catch (SQLException ex) {
            JOptionPane.showMessageDialog(null, ex);
        }
     
// TODO add your handling code here:
    }//GEN-LAST:event_kodeBarangActionPerformed

