    private void jumlahBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jumlahBtnActionPerformed
       int ntap1 = Integer.parseInt(kodeBarang1.getText());
       int ntap2 = Integer.parseInt(kodeBarang2.getText());
       int ntap3 = Integer.parseInt(jumlahUang.getText());
       int hasil = ntap3 - (ntap1*ntap2);
       kodebarang3.setText(Integer.toString(hasil));
        // TODO add your handling code here:
    }//GEN-LAST:event_jumlahBtnActionPerformed

